jQuery.sap.declare("formatter");formatter={formatDate:function(e){if(e&&e!=="00000000"){return sap.ui.core.format.DateFormat.getDateInstance({pattern:"MMM dd, yyyy"}).format(new Date(e.substring(4,6)+"/"+e.substring(6,8)+"/"+e.substring(0,4)))}else{return""}},dateFormat:function(e){if(e){return sap.ui.core.format.DateFormat.getDateInstance({pattern:"MMM dd, yyyy"}).format(new Date(e))}else{return""}},formatTime:function(e){if(e){return e.toLocaleTimeString()}else{return""}},formatStatus:function(e){var t="";if(e){switch(e){case"HAP":t="HOD Approval Pending";break;case"ABH":t="Approved by HOD & Pending with Finance";break;case"RBH":t="Rejected by HOD";break;case"ABF":t="Approved by Finance";break;case"RBF":t="Rejected by Finance";break}}return t},statusState:function(e){var t="None";if(e){switch(e){case"ABF":t="Success";break;case"HAP":case"ABH":t="Warning";break;default:t="Error";break}}return t},checkApprovalAccess:function(e,t){const r=this.getModel("FinModel").getData(),a=this.getModel().getHeaders().loginId;if(r.length>0){if(e==="HAP"&&t===a){return true}else if(e==="ABH"&&r.findIndex(e=>e.FinEmail===this.getModel().getHeaders().loginId)!==-1){return true}else{return false}}else{return false}},invNoLink:function(e,t){if(e&&t){if((e==="RBH"||e==="RBF")&&sap.ui.getCore().loginEmail===t){return true}else{return false}}else{return false}},addBtnVisible:function(){if(this.getModel().getHeaders().loginType==="P"){return true}else{return false}}};